mapper.select:
/* getAPILogTotalCount */ SELECT COUNT(api_log_id) FROM api_log
AND ${search_word} = #{search_value}
AND ${search_word} LIKE '%' || #{search_value} || '%'
AND status_code >= #{search_status}::int AND status_code < #{search_status}::int+100
AND insert_date >= TO_TIMESTAMP(#{search_start_date}, 'YYYYMMDDHH24MISSUS')
AND insert_date <= TO_TIMESTAMP(#{search_end_date}, 'YYYYMMDDHH24MISSUS')

/* getListAPILog */
SELECT * FROM api_log


	AND ${search_word} = #{search_value}


	AND ${search_word} LIKE '%' || #{search_value} || '%'



	AND status_code >= #{search_status}::int AND status_code < #{search_status}::int+100




	AND insert_date >= TO_TIMESTAMP(#{search_start_date}, 'YYYYMMDDHH24MISSUS')




	AND insert_date <= TO_TIMESTAMP(#{search_end_date}, 'YYYYMMDDHH24MISSUS')





	ORDER BY ${order_word} ${order_value}


	ORDER BY insert_date DESC, api_log_id DESC


OFFSET #{offset} LIMIT #{limit}

mapper.insert:
		/* insertAPILog */

    		SELECT NEXTVAL('api_log_seq')

		INSERT INTO api_log (
			api_log_id, client_id, client_name, request_ip, user_id, url, status_code, message
		) VALUES (
			#{api_log_id}, #{client_id}, #{client_name}, #{request_ip}, #{user_id}, #{url}, #{status_code}, #{message}
		)
