mapper.insert:
		/* insertAPILog */

    		SELECT NEXTVAL('api_log_seq')

		INSERT INTO api_log (
			api_log_id, client_id, client_name, client_ip, user_id, url, status_code, message
		) VALUES (
			#{api_log_id}, #{client_id}, #{client_name}, #{client_ip}, #{user_id}, #{url}, #{status_code}, #{message}
		)
