mapper.insert:
		/* insertOrthoImage */

    		SELECT NEXTVAL('ortho_image_seq')

		INSERT INTO ortho_image (
			ortho_image_id, transfer_data_id, file_name, file_real_name, file_path, file_size, file_ext
		) VALUES (
			#{ortho_image_id}, #{transfer_data_id}, #{file_name}, #{file_real_name}, #{file_path}, #{file_size}, #{file_ext}
		)

mapper.update:
/* updateOrthoImage */
UPDATE ortho_image
SET status = #{status},
	update_date = now()
WHERE ortho_image_id = #{ortho_image_id}
