mapper.insert:
		/* insertDroneProject */

    		SELECT NEXTVAL('simulation_log_seq')

		INSERT INTO simulation_log (
			simulation_log_id, simulation_type, client_id, client_name
		) VALUES (
			#{simulation_log_id}, #{simulation_type}, #{client_id}, #{client_name}
		)

mapper.select:
/* getSimulationLogList */
SELECT * FROM simulation_log


	AND client_name = #{search_value}


	AND client_name LIKE '%' || #{search_value} || '%'


	AND simulation_type = #{search_type}


	AND status = #{search_status}



	AND ${search_date} >= TO_TIMESTAMP(#{search_start_date}, 'YYYYMMDDHH24MISSUS')




	AND ${search_date} <= TO_TIMESTAMP(#{search_end_date}, 'YYYYMMDDHH24MISSUS')





	ORDER BY ${order_word} ${order_value}


	ORDER BY start_date DESC, simulation_log_id DESC


OFFSET #{offset} LIMIT #{limit}

/* getSimulationLogTotalCount */
SELECT COUNT(simulation_log_id)
FROM simulation_log


	AND client_name = #{search_value}


	AND client_name LIKE '%' || #{search_value} || '%'


	AND simulation_type = #{search_type}


	AND status = #{search_status}



	AND ${search_date} >= TO_TIMESTAMP(#{search_start_date}, 'YYYYMMDDHH24MISSUS')




	AND ${search_date} <= TO_TIMESTAMP(#{search_end_date}, 'YYYYMMDDHH24MISSUS')
