mapper.select:
/* getListUploadLog */
SELECT *
FROM upload_log 
OFFSET #{offset} LIMIT #{limit}

mapper.insert:
		/* insertFileInfo */

    		SELECT NEXTVAL('upload_log_seq')

		INSERT INTO upload_log(
			upload_log_id, file_name, file_real_name, file_path, file_size, file_ext
		) values(
			#{upload_log_id}, #{file_name}, #{file_real_name}, #{file_path}, #{file_size}, #{file_ext}
		)
