mapper.select:
/* getUserDeviceByUserId */
SELECT *
FROM user_device
WHERE user_id = #{user_id}

mapper.insert:
/* insertUserDevice */
SELECT NEXTVAL('user_device_seq')
INSERT INTO user_device ( user_device_id, user_id, device_name1, device_type1, device_ip1, device_priority1, use_yn1, device_name2, device_type2, device_ip2, device_priority2, use_yn2, device_name3, device_type3, device_ip3, device_priority3, use_yn3, device_name4, device_type4, device_ip4, device_priority4, use_yn4, device_name5, device_type5, device_ip5, device_priority5, use_yn5 ) VALUES ( #{user_device_id}, #{user_id}, #{device_name1}, #{device_type1}, #{device_ip1}, 1, #{use_yn1}, #{device_name2}, #{device_type2}, #{device_ip2}, 2, #{use_yn2}, #{device_name3}, #{device_type3}, #{device_ip3}, 3, #{use_yn3}, #{device_name4}, #{device_type4}, #{device_ip4}, 4, #{use_yn4}, #{device_name5}, #{device_type5}, #{device_ip5}, 5, #{use_yn5} )
